import React, { useState } from 'react';
import { Box, Button, Typography } from '@mui/material';
import UrlInputForm from './UrlInputForm';
import axios from 'axios';

const ShortenerPage = ({ onNavigate }) => {
  const [shortUrl, setShortUrl] = useState('');
  const [error, setError] = useState('');

  const handleUrlSubmit = async (formData) => {
    try {
      const response = await axios.post('/evaluation-service/shorten', formData);
      setShortUrl(response.data.shortUrl);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Failed to shorten URL.');
    }
  };

  return (
    <Box>
      <UrlInputForm onSubmit={handleUrlSubmit} />
      {shortUrl && (
        <Box mt={2}>
          <Typography variant="h6">Shortened URL:</Typography>
          <Typography variant="body1" color="primary">{shortUrl}</Typography>
        </Box>
      )}
      {error && <Typography color="error">{error}</Typography>}
      <Button variant="outlined" sx={{ mt: 2 }} onClick={() => onNavigate('stats')}>
        View Stats
      </Button>
    </Box>
  );
};

export default ShortenerPage;
