import React, { useState } from "react";
import UrlInputForm from "./UrlInputForm";
import axios from "axios";
import { validateInputs } from "../utils/validation";

const ShortenerPage = () => {
  const [formData, setFormData] = useState([
    { originalUrl: "", validity: "", shortcode: "" },
  ]);
  const [message, setMessage] = useState("");

  const handleChange = (index, field, value) => {
    const newFormData = [...formData];
    newFormData[index][field] = value;
    setFormData(newFormData);
  };

  const handleAdd = () => {
    setFormData([...formData, { originalUrl: "", validity: "", shortcode: "" }]);
  };

  const handleSubmit = async () => {
    const validationError = validateInputs(formData);
    if (validationError) {
      setMessage(validationError);
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/shorten", formData);
      setMessage(res.data.message || "Shortened successfully");
    } catch (err) {
      setMessage("Error: " + (err.response?.data?.message || "Failed to shorten URLs"));
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>URL Shortener</h2>
      {formData.map((item, index) => (
        <UrlInputForm
          key={index}
          index={index}
          data={item}
          onChange={handleChange}
        />
      ))}
      <button onClick={handleAdd}>Add Another</button>
      <button onClick={handleSubmit} style={{ marginLeft: "10px" }}>Submit</button>
      <div style={{ marginTop: "10px", color: "green" }}>{message}</div>
    </div>
  );
};

export default ShortenerPage;
